class UnionFind {
public: 
    vector<int> parent, rank; 
    UnionFind(int n) {
        parent.resize(n); 
        iota(parent.begin(), parent.end(), 0); 
        rank.resize(n); 
        fill(rank.begin(), rank.end(), 1); 
    }

    int find(int p) {
        if (p != parent[p]) 
            parent[p] = find(parent[p]); 
        return parent[p]; 
    }

    bool connect(int p, int q) {
        int prt = find(p), qrt = find(q); 
        if (prt == qrt) return false; 
        if (rank[prt] > rank[qrt]) swap(prt, qrt); 
        parent[prt] = qrt; 
        rank[qrt] += rank[prt]; 
        return true; 
    }
}; 


class Solution {
public:
    int largestComponentSize(vector<int>& nums) {
        int m = *max_element(nums.begin(), nums.end()); 
        vector<bool> seen(m+1); 
        for (auto& x : nums) seen[x] = true; 
        UnionFind* uf = new UnionFind(m+1); 
        vector<bool> sieve(m+1, true); 
        sieve[0] = sieve[1] = false; 
        for (int x = 2; x <= m/2; ++x) 
            if (sieve[x]) {
                int prev = seen[x] ? x : 0; 
                for (int xx = 2*x; xx <= m; xx += x) {
                    sieve[xx] = false; 
                    if (seen[xx]) 
                        if (prev) uf->connect(prev, xx); 
                        else prev = xx; 
                }
            }
        int ans = *max_element(uf->rank.begin(), uf->rank.end()); 
        delete uf; 
        return ans; 
    }
};
