class Solution {
public:
    int find(int x, vector<int> &parent)
    {
        if(x==parent[x])
        {
            return x;
        }
        return parent[x] = find(parent[x],parent);
    }
    void merge(int a,int b,vector<int> &parent,vector<int> &rank)
    {
        int pa = find(a,parent);
        int pb = find(b,parent);
        if(pa!=pb)
        {
            if(rank[pa]<rank[pb])
            {
                swap(pa,pb);
            }
            parent[pb] = pa;
            if(rank[pa]==rank[pb])
            {
                rank[pa]++;
            }
        }
    }

    unordered_set<int> get_factors(int n, vector<int> &sieve)
    {
        unordered_set<int> f;
        while(n>1)
        {
            f.insert(sieve[n]);
            n = n/sieve[n];
        }
        return f;
    }

    int largestComponentSize(vector<int>& nums) {
        int n = nums.size();
        int maxx = *max_element(nums.begin(),nums.end());
        vector<int> parent(maxx+1);
        for(int i =0;i<=maxx;i++)
        {
            parent[i] = i;
        }
        vector<int> rank(maxx+1,0);

        //create the factor sieve
        vector<int> sieve(maxx+1);
        for(int i=0;i<=maxx;i++)
        {
            sieve[i] = i;
        }
        for(int i=2;(i*i)<=maxx;i++)
        {
            if(sieve[i]==i)
            {
                for(int j = i*i;j<=maxx;j+=i)
                {
                    if(sieve[j]==j)
                    {
                        sieve[j] = i;
                    }
                }
            }
        }
        //

        for(int i=0;i<n;i++)
        {
            int curr = nums[i];
            unordered_set<int> fac = get_factors(curr,sieve);
            for(int f:fac)
            {
                merge(f,curr,parent,rank);
            }
        }
        map<int,int> mp;
        int ans= 1;
        for(int i=0;i<n;i++)
        {
            int pc = find(nums[i],parent);
            mp[pc]++;
            ans = max(ans,mp[pc]);
        }
        return ans;
    }
};
