class Solution {
    static class UF {
        int[] size;
        int[] parent;
        int max;

        public UF(int n) {
            size = new int[n];
            parent= new int[n];
            for(int i=0; i<n; i++) {
                parent[i] = i;
                size[i] = 1;
            }
        }

        int find(int x) {
            int temp = x;
            while(parent[x] != x) {
                x = parent[x];
            }

            while(temp != x) {
                int ptemp = parent[temp];
                parent[temp] = x;
                temp = ptemp;
            }
            return x;
        }

        public void union(int x, int y) {
            int px = find(x);
            int py = find(y);
            if(px == py) return;

            if(size[px] > size[py]) {
                size[px] += size[py];
                parent[py] = px;
                max= Math.max(max, size[px]);
            } else {
                size[py] += size[px];
                parent[px] = py;
                max= Math.max(max, size[py]);
            }
        }
    }
    public int largestComponentSize(int[] A) {
        int n = A.length;
        UF uf = new UF(n);
        Map<Integer, Integer> map = new HashMap<>();

        for(int i=0; i<n; i++) {
            int num = A[i];
            // to compute the factorization we need to go upto sqrt(num) times
            for(int j=2; j*j <=num; ++j) {
                // if j is one of the factor of num
                if(num%j == 0) {
                    if(map.containsKey(j)){
                        uf.union(i, map.get(j));
                    } else {
                        map.put(j, i);
                    }

                    if(map.containsKey(num/j)){
                        uf.union(i, map.get(num/j));
                    } else {
                        map.put(num/j, i);
                    }
                }

            }

            // if factor is not possible
            // then number itself is an factor
            if(map.containsKey(num)) {
                uf.union(i, map.get(num));
            } else {
                map.put(num, i);
            }
        }

        return uf.max;
    }
}
