class UF:        
    def __init__(self) -> None:
        self.parent = {}
        self.sz = {}
        self.count = 0

    def add(self, p):    
        if p not in self.parent:
            self.parent[p] = p
            self.sz[p] = 1
            self.count += 1

    def find(self, p: int) -> int:
        while p != self.parent[p]:
            self.parent[p] = self.parent[self.parent[p]]
            p = self.parent[p]
        return p  

    def union(self, p: int, q: int) -> None:
        i = self.find(p)
        j = self.find(q)             
        if i == j:    
            return 
        if self.sz[i] > self.sz[j]:                 
            self.parent[j] = i
            self.sz[i] += self.sz[j]
        else:             
            self.parent[i] = j
            self.sz[j] += self.sz[i]                                           
        self.count -= 1 

class Solution:
