int find_root(vector<int>&id,int i){
    while(i!=id[i]){
        id[i]=id[id[i]]; //pathcompression;
        i=id[i];
    }
    return i;
}

class Solution {
public:
    int largestComponentSize(vector<int>& A) {
        map<int,vector<int>>mymap;
        vector<int>id(A.size());
        vector<int>size(A.size(),1);
        for(int i=0;i<A.size();i++){
            id[i]=i;
        }

        int n;

        for(int i=0;i<A.size();i++){
            n=A[i];
            bool temp=false;
            while(n%2==0){
                n=n/2;
                temp=true;
             }
            if(temp==true) mymap[2].push_back(i);
            for(int k=3;k<=sqrt(n);k++){

                temp=false;
                while(n%k==0){
                    n=n/k;
                    temp=true;
                }

                if(temp==true) mymap[k].push_back(i);
            }

            if(n>2){
                mymap[n].push_back(i);
            }

        }

        int maxi=1;

        for(auto it=mymap.begin();it!=mymap.end();it++){

            for(int k=0;k<it->second.size()-1;k++){
                int pa=find_root(id,it->second[k]);
                int pb=find_root(id,it->second[k+1]);
                if(pa!=pb){

                    if(size[pa]>size[pb]){
                        id[pb]=pa;
                        size[pa]+=size[pb];
                        maxi=max(maxi,size[pa]);
                    }
                    else{
                        id[pa]=pb;
                        size[pb]+=size[pa];
                        maxi=max(maxi,size[pb]);
                    }

                }

            }

        }
        return maxi;   
    }
};
