class Solution(object):
    def largestComponentSize(self, A):
        """
        :type A: List[int]
        :rtype: int
        """
        parent = range(len(A))
        # Union Find
        def Findparent(i):
            if parent[i] != i:
                parent[i] = Findparent(parent[i])
            return parent[i]

        # step 1: Sieve of Eratosthenes, only consider the prime number <= m ** 0.5 
        m = int(max(A) ** 0.5) + 1
        R = [1] * m
        prime = []
        for i in xrange(2, m):
            if R[i] == 1:
                prime.append(i)
            for j in xrange(i*i, m, i):
                R[j] = 0

        # step 2: check every prime number and do some prunes to narrow the search space
        B = range(len(A))
        for p in prime:
            tempB = []
            L = -1
            # print A, B, parent
            for idx in B:
                if A[idx] % p == 0:
                    if L == -1:
                        L = idx
                        a = Findparent(L)
                    else:
                        b = Findparent(idx)
                        parent[b] = a
                    while A[idx] % p == 0:
                        A[idx] /= p      
                if A[idx] > p:
                    tempB.append(idx)     
            B = tempB

        # step 3: deal with the number with primes > m ** 0.5 as its factors
        D = {}
        for idx in B:
            if A[idx] not in D:
                D[A[idx]] = []
            D[A[idx]].append(idx)
        # print D
        for i in D:
            if len(D[i]) > 1:
                a = Findparent(D[i][0])
                for j in xrange(1,len(D[i])):
                    b = Findparent(D[i][j])
                    parent[b] = a

        # step 4: do the counting
        D = {}
        for i in xrange(len(parent)):
            a = Findparent(i)
            if a not in D:
                D[a] = 0
            D[a] += 1
        return max(D.values())
