class Solution {
public:
    vector<int> p;
    vector<int> r;
    int gcd(int a , int b)
    {
          return b == 0 ? a : gcd(b, a % b);
    }

    int find(int k)
    {
        if(p[k] == k)
            return k;
        return p[k] = find(p[k]);
    }

    void unon(int a, int b)
    {
        int pa = find(a);
        int pb = find(b);
        if(pa == pb)
            return;
        if(r[pa]>r[pb]){
            p[pb] = pa;
            r[pa]+=r[pb];
        }
        else{
            p[pa] = pb;
            r[pb]+=r[pa];
        }
    }

    int largestComponentSize(vector<int>& nums) {
        int ii = 0;
        p.resize(100002);
        r.resize(100002);
        for(int i=0;i<100002;i++)
        {
            p[i] = i;
            r[i] = 1;
        }



        int n = nums.size();

        for(int i=0;i<n;i++)
            for(int j=2;j<=sqrt(nums[i]);j++)
            {
                if(nums[i]%j==0)
                {
                    unon(nums[i],j);
                    unon(nums[i],nums[i]/j);
                }
            }


        unordered_map<int, int> mp;
        for(auto i: nums)
        {
            int y = find(i);
            mp[y]++;
        }
        int res = 0;
        for(auto i: mp)
        {
            res = max(res,i.second);
        }
        return res;
    }
};
