class Solution {
    public int largestComponentSize(int[] A) {

        int maxValue = Arrays.stream(A).reduce(A[0], (x, y) -> x > y ? x : y);

        UnionFind uf = new UnionFind(maxValue + 1);

        // Attribute each element to all the groups that lead by its factors.
        for (int num : A) {
            for (int factor = 2; factor < (int)(Math.sqrt(num)) + 1; ++factor)
                if (num % factor == 0) {
                    uf.union(num, factor);
                    uf.union(num, num / factor);
                }
        }

        int maxGroupSize = 0;
        HashMap<Integer, Integer> groupCount = new HashMap<>();
        for (int num : A) {
            Integer groupId = uf.find(num);
            Integer count = groupCount.getOrDefault(groupId, 0);
            groupCount.put(groupId, count+1);
            maxGroupSize = Math.max(maxGroupSize, count+1);
        }

        return maxGroupSize;
    }
}

class UnionFind {
    int[] parents;
    int[] ranks;

    public UnionFind(int n) {
        this.parents = new int[n];
        this.ranks = new int[n];

        for (int i = 0; i < n; i++) {
            this.parents[i] = i;
            this.ranks[i] = 1;
        }
    }

    public int find(int x) {
        if (parents[x] != x) parents[x] = find(parents[x]);

        return parents[x];
    }

    public void union(int x, int y) {
        int xRoot = find(x);
        int yRoot = find(y);

        if (xRoot == yRoot) return;

        if (ranks[xRoot] < ranks[yRoot]) {
            parents[xRoot] = yRoot;
        } else {
            parents[yRoot] = xRoot;
        }

        if (ranks[xRoot] == ranks[yRoot]) {
            ranks[xRoot]++;
        }
    }
}
