# Union Find Set  
class UnionFindSet: 
	def __init__(self, n): 
		self.parents = [i for i in range(n+1)]
		self.ranks = [1 for _ in range(n+1)]

	def find(self, u): 
		while u != self.parents[u]: 
			self.parents[u] = self.parents[self.parents[u]]
			u = self.parents[u]
		return u

	def union(self, u, v): 
		u_parent = self.find(u)
		v_parent = self.find(v)
		if u_parent == v_parent: 
			return False

		if self.ranks[u_parent] > self.ranks[v_parent]:   
			self.parents[v_parent] = u_parent
		elif self.ranks[u_parent] < self.ranks[v_parent]: 
			self.parents[u_parent] = v_parent
		elif self.ranks[u_parent] == self.ranks[v_parent]: 
			self.parents[v_parent] = u_parent
			self.ranks[u_parent] += 1
		return True    


class Solution(object):
	def largestComponentSize(self, nums):
		"""
		:type nums: List[int]
		:rtype: int
		"""
		max_num = max(nums)
		union_find_set = UnionFindSet(max_num+1)

		for num in nums: 
			sqrt_num = int(num**(1/2.0))+1
			for i in range(2, sqrt_num): 
				if num%i==0: 
					union_find_set.union(num, i) 
					union_find_set.union(num, num/i)

		from collections import defaultdict
		res = defaultdict(int)            
		for num in nums: 
			res[union_find_set.find(num)] += 1
		# print(res.values())    
		return max(res.values())

