        if(ds[x]==0)
            return x;
        return find(ds[x],ds);
    }



    public int largestComponentSize(int[] nums) {
        int max=Integer.MIN_VALUE;
        for(int i:nums)
            max=Math.max(max,i);
          int ds[]=new int[max+1];
          int size[]=new int[max+1];
        Arrays.fill(size,1);

        for(int i=0;i<nums.length;i++){
            int curr=nums[i];
            for(int j=2;j<=Math.sqrt(curr);j++){
                if(curr%j==0){
                    int x=find(curr,ds);
                    int y=find(j,ds);
                    if(x!=y){
                        if(size[x]>size[y])
