class Solution {
public:
	/*function to get root or parent*/
    int getRoot(vector<int>& root, int x)
    {
        if(x == root[x])
            return x;
        else
            return root[x] = getRoot(root, root[x]); 
    }
	/*get all prime factors of x in a vector*/
     vector<int> getPrimes(int x, vector<int>& s) {
        vector<int> primes;
        while (x > 1) {
            int p = s[x];
            if(p == 0)
                p = x;
            primes.push_back(p);
			// Edit: uncomment next 2 lines and comment last x/=p to remove duplicate same primes, taken from other solution.
			// while(x % p == 0)
            //    x /= p;
            x /= p;
        }
        return primes;
    }
	/*union a and b by making roots same*/
    void unions(int a, int b, vector<int>& root)
    {
        int aR = getRoot(root, a);
        int bR = getRoot(root, b);
        if(aR > bR)  // if a is greater, make root of b as a
            root[bR] = root[aR];
        else
            root[aR] = root[bR];
    }
    int largestComponentSize(vector<int>& nums) {
        int n=nums.size();
        int i,j,M=0;
		int ans=1; //minimum answer can be 1.
        for(i=0;i<n;i++)
        {
            M = max(M,nums[i]); // get the maximum number.
        }
        vector<int>sieve(M+1); // used for sieve of eratosthenes
        vector<int>count(M+1);  // count_i stores the count of all numbers having root = i
        vector<int>root(M+1);  // stores the root
        for(i=0;i<=M;i++)
        {
            root[i] = i; // make each number its on root
        }
        for(i=2;i*i<=M;i++)
        {
            if(sieve[i]) // prime number will have 0 value
                continue;
            for(j=i*i;j<=M;j+=i)
            {
                sieve[j] = i; // the smallest prime value will be stored here
            }
        }
        for(i=0;i<n;i++)
        {
            if(nums[i]==1) //ignore 1
                continue;
            vector<int>v = getPrimes(nums[i], sieve); //get the prime factors
            for(j=0; j<v.size()-1; j++)
            {
                unions(v[j], v[j+1], root); // union them i.e make their root same.
            }
            unions(v[j], nums[i], root); // union the number with one of their factors
        }
        for(i=0;i<n;i++)
        {
            int r = getRoot(root, nums[i]); // find the root i.e. the bucket in which this number will fall
            count[r]++; //increase the count of that bucket
            ans = max(ans, count[r]); // check max
        }
        return ans; // return the ans
    }
};
