public class Solution {
    public int LargestComponentSize(int[] nums) {
        var set = new UnionFindSet(nums.Max() + 1);

        // Calculate primes set for all elements
        foreach (var num in nums) {
            for (var k = 2; k * k <= num; ++k) {
                if (num % k == 0) {
                    set.Union(num, k);
                    set.Union(num, num / k);
                }
            }
        }

        // Count the apperance of parents, return the maxium one.
        // All connected nodes will point to same parent
        var map = new Dictionary<int, int>();
        var result = 1;
        foreach (var num in nums) {
            var count = set.Find(num);
            map[count] = (map.ContainsKey(count) ? map[count] : 0) + 1;
            result = Math.Max(result, map[count]);
        }        
        return result;
    }

    public class UnionFindSet {
        private int[] parent;

        public UnionFindSet(int size) {
            parent = new int[size];
            for (int i = 0; i < parent.Length; ++i) {
                parent[i] = i;
            }
        }
        public void Union(int x, int y) {
            parent[Find(x)] = parent[Find(y)];
        }
        public int Find(int x) {
            if (parent[x] != x) {
                parent[x] = Find(parent[x]);
            }
            return parent[x];
        }        
    }
}
