public class Solution {
    public class UnionFind {
        private int[] root;
        private int[] rank;

        // O(n)
        public UnionFind(int vertices){
            root = new int[vertices];
            rank = new int[vertices];
            for(int i = 0; i < vertices; i++){
                root[i] = i;
                rank[i] = 1;
            }
        }

        // O(1), average
        public int Find(int x){
            if(x == root[x]){
                return x;
            }
            return root[x] = Find(root[x]);
        }

        // O(1), average
        public void Union(int x, int y){
            int xRoot = Find(x);
            int yRoot = Find(y);
            if(xRoot != yRoot){
                if(rank[xRoot] > rank[yRoot]){
                    root[yRoot] = xRoot;
                } else if(rank[xRoot] < rank[yRoot]){
                    root[xRoot] = yRoot;
                } else {
                    root[yRoot] = xRoot;
                    rank[xRoot]++;
                }
            }
        }
    }

    public int LargestComponentSize(int[] nums) {
        int maxValue = nums.Aggregate((x, y) => x > y ? x : y); // Uses LINQ's reduce method to find largest value
        UnionFind ds = new UnionFind(maxValue + 1); // +1 to hold just enough items, without +1 out of bounds

        foreach(int num in nums){
            for(int factor = 2; factor < (int)(Math.Sqrt(num)) + 1; ++factor){
                if(num % factor == 0){
                    ds.Union(num, factor);
                    ds.Union(num, num / factor);
                }
            }
        }

        int maxGroupSize = 0;
        Dictionary<int, int> groupCount = new Dictionary<int, int>();
        foreach(int num in nums){
            int groupID = ds.Find(num);
            if(!groupCount.ContainsKey(groupID)){
                groupCount.Add(groupID, 0);
            }
            groupCount[groupID]++;
            maxGroupSize = Math.Max(maxGroupSize, groupCount[groupID]);
        }

        return maxGroupSize;
    }
}
