class Uni:

    def __init__(self, arr):
        self.arr = {i:i for i in arr}
        self.sz = {i:1 for i in arr}

    def find(self, x):
        if x != self.arr[x]: self.arr[x] = self.find(self.arr[x])
        return self.arr[x]

    def same(self, x, y):
        return self.find(x) == self.find(y)

    def merge(self, x, y):
        x, y = self.find(x), self.find(y)
        if x == y: return
        if x > y: x, y = y, x
        self.sz[x] += self.sz[y]
        self.arr[y] = self.arr[x]


class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:
        uni = Uni(nums)
        bound = max(nums) + 1
        isprime = [True] * bound
        nums = set(nums)
        for i in range(2, bound):
            if not isprime[i]: continue
            last = None
            for j in range(i, bound, i):
                isprime[j] = False
                if j in nums:
                    if last is None: last = j
                    else: uni.merge(last, j)
        return max(uni.sz.values())

