class UnionFind {
    public:
        vector < int > par, SIZ;
        UnionFind( int sz = 100005 ) {
            for ( int i = 0; i < sz; ++i ) {
                par.push_back( i );
                SIZ.push_back( 0 );
            }
        }

        int find_root( int u ) {
            if ( par[ u ] != u )
                par[ u ] = find_root( par[ u ] );

                return par[ u ];
        }

        void merge( int u, int v ) {
            if ( find_root( u ) != find_root( v ) ) {
                if ( SIZ[ par[ u ] ] <= SIZ[ par[ v ] ] )   swap( u, v );

                SIZ[ par[ u ] ] += SIZ[ par[ v ] ];
                par[ par[ v ] ] = par[ u ];
            }
        }
};

class Sieve {
    public:     
        vector < int > sie;
        vector < int > spf;  

        Sieve( int MSS ) {    
            spf.resize( MSS + 7, 0 );
            for ( int i = 2; i < MSS; ++i ) {  
                if ( spf[ i ] == 0 ) {
                    spf[ i ] = i;   
                    sie.push_back( i );  
                }    
                for ( int j = 0; j < sie.size() && i * sie[ j ] <= MSS && sie[ j ] <= spf[ i ]; ++j ) 
                    spf[ i * sie[ j ] ] = sie[ j ]; 
            }
        }
};

class Solution {
    public:
        Sieve Sv = Sieve( 100005 );

        int largestComponentSize( vector < int > &Arr ) {
            UnionFind UF = UnionFind();

            for ( int A : Arr ) {
                if ( A == 1 ) {
                    UF.SIZ[ A ] = 1;
                    continue;
                }
                vector < int > E;
                while ( A != 1 ) {
                    E.push_back( Sv.spf[ A ] );
                    A /= Sv.spf[ A ]; 
                }

                UF.SIZ[ UF.find_root( E[ 0 ] ) ]++;

                for ( int i = 1; i < E.size(); ++i ) 
                    UF.merge( E[ 0 ], E[ i ] );
            }

            return *max_element( UF.SIZ.begin(), UF.SIZ.end() );
        }
};
