class Solution {
public:
    vector<int>v[100005];
    bool seen[100005];
    bool have[100005];
    int cnt=0;
    void dfs(int x)
    {
        if(seen[x])return;seen[x]=1;
        if(have[x]){
            cnt++;

        }
        for(int i=0;i<v[x].size();i++)dfs(v[x][i]);
    }
    int largestComponentSize(vector<int>& A) {
        for(int i=0;i<A.size();i++)
        {
            int take=A[i];
            have[take]=1;
            if(take<2)continue;
            for(long j=2;j*j<=(long)take;j++)
            {

                if(take%j==0) {
                    while(take%j==0){
                        take=take/j;
                    }
                    v[A[i]].push_back(j);
                    v[j].push_back(A[i]);
                }
            }
            if(take>1){
                v[take].push_back(A[i]);
                v[A[i]].push_back(take);
            }
        }
        int mx=0;
        for(int i=0;i<A.size();i++) 
        {
            if(seen[A[i]]==0){
                cnt=0;
                dfs(A[i]);

                mx=max(mx,cnt);
            }
        }
        return mx;
    }
};
