class Solution {
    Map<Integer,Integer>map=new HashMap<>();
    Map<Integer,Integer>rootmap=new HashMap<>();
    int nums[];
    public int largestComponentSize(int[] A) {
        int max=0;
        int res=0;
        for(int n:A)max=Math.max(max,n);
        nums=new int[max+1];
        for(int i=0;i<nums.length;i++)nums[i]=i;
        for(int n:A){
            List<Integer>list=new ArrayList<>();
            for(int i=2;i*i<=n;i++){
                if(n%i==0){
                    list.add(i);
                    list.add(n/i);
                }
            }
            list.add(n);
            for(int i=1;i<list.size();i++){
                int r1=find(nums,list.get(i-1));
                int r2=find(nums,list.get(i));
                if(r1!=r2)nums[r2]=r1;
            }
            int last=list.get(list.size()-1);
            if(!map.containsKey(last))map.put(last,0);
            map.put(last,map.get(last)+1);
        }
        for(Integer prime:map.keySet()){
            int r=find(nums,prime);
            if(!rootmap.containsKey(r))rootmap.put(r,0);
            rootmap.put(r,rootmap.get(r)+map.get(prime));
        }
        for(Integer key:rootmap.keySet()){
            res=Math.max(res,rootmap.get(key));
        }
        return res;
    }
    public int find(int nums[],int x){
        if(nums[x]==x)return x;
        int root=find(nums,nums[x]);
        nums[x]=root;
        return root;
    }
}
