class UnionFind {
  private int[] id;
  private int[] sz;

  public UnionFind(int n) {
    this.id = new int[n];
    this.sz = new int[n];
    for(int i = 0; i < n; i++){
      this.id[i] = i;
      this.sz[i] = 1;
    }
  }

  public int LargestSize() {
    int max = 1;
    for(int i = 0; i < this.sz.Length; i++) {
      if(this.sz[i] > max) max = this.sz[i];
    }
    return max;
  }

  public void Unite(int p, int q) {
    int i = this.Root(p);
    int j = this.Root(q);

    if (i == j) return;

    if(this.sz[i] < this.sz[j]) {
      this.id[i] = j;
      this.sz[j] += this.sz[i];
    } else {
      this.id[j] = i;
      this.sz[i] += this.sz[j];
    }
  }

  private int Root(int i) {
    while(i != this.id[i]){
      this.id[i] = this.id[this.id[i]];
      i = this.id[i];
    }
    return i;
  }
}

public class Solution {
  public int LargestComponentSize(int[] A) {
    var lowest_factor = new int[100001];
    initializeSieve(lowest_factor);

    var uf = new UnionFind(A.Length);

    var primeToIndex = new int[100001];
    Array.Fill(primeToIndex, -1);

    for(int i = 0; i < A.Length; i++) {
      int x = A[i];
      while(x > 1){
        int p = lowest_factor[x];

        if(primeToIndex[p] == -1) {
          primeToIndex[p] = i;
        } else {
          uf.Unite(i, primeToIndex[p]);
        }

        while(x % p == 0) x /= p;
      }
    }

    return uf.LargestSize();
  }

  private void initializeSieve(int[] lowest_factor) {
    for(int i = 0; i <= 100000; i++) lowest_factor[i] = -1;

    for(int i = 2; i <= 100000; i++) {
      if (lowest_factor[i] == -1) {
        for(int j = i; j <= 100000; j = j + i) lowest_factor[j] = i;
      }
    }
  }
}
