class UnionFind:
    def __init__(self, n):
        self.n = n
        self.parents = [i for i in range(n)]
        self.childrenNum = n * [1]

    def find(self, i):
        if self.parents[i] == i:
            return i

        return self.find(self.parents[i])

    def union(self, i, j):
        x = self.find(i)
        y = self.find(j)
        if x != y:
            self.parents[y] = x
            self.childrenNum[x] += self.childrenNum[y]
            self.childrenNum[y] = 0

    def getLargestComponent(self):
        return max(self.childrenNum)

    def getSize(self):
        s = set()
        for i in range(self.n):
            s.add(self.find(i))
        return len(s)

class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:
        def primeset(n):
            for i in range(2, int(sqrt(n))+1):
                if n % i == 0:
                    return primeset(n // i) | {i}

            return {n}

        factorset = {}
        n = len(nums)
        uf = UnionFind(n)
        for i in range(n):
            fs = primeset(nums[i])
            for factor in fs:
                if factor not in factorset:
                    factorset[factor] = [i]

                else:
                    factorset[factor].append(i)

        for key, values in factorset.items():
            parent = values[0]
            for idx in values[1:]:
                uf.union(parent, idx)

        return uf.getLargestComponent()
