class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:
      n,prime,isPresent = max(nums) + 11,[],set(nums) 
      g,ans,curr = [[] for _ in range(n)],0,0


      def sieve(n):
        primes = [True for _ in range(n)] 
        for i in range(2,n):
          if primes[i] : 
            for j in range(i*i,n,i): primes[j] = False
            prime.append(i) 

      sieve(n) 
      for number in prime : 
        for x in range(2*number,n,number):
          if x in isPresent : 
            g[x].append(number) 
            g[number].append(x) 

      visited = [False for _ in range(n)] 
      def dfs(x):
        nonlocal curr
        if x in isPresent : curr += 1 
        visited[x] = True 
        for i in g[x] : 
          if not visited[i] : dfs(i) 

      for i in range(n):
        curr = 0 
        if not visited[i]:
          dfs(i) 
          ans = max(ans ,curr) 

      return ans



