from collections import defaultdict
from math import gcd

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])  # Path compression
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)

        if root_x == root_y:
            return False  # Both elements are already in the same set

        # Union by rank
        if self.rank[root_x] < self.rank[root_y]:
            self.parent[root_x] = root_y
        elif self.rank[root_x] > self.rank[root_y]:
            self.parent[root_y] = root_x
        else:
            self.parent[root_y] = root_x
            self.rank[root_x] += 1
        return True  # Elements were successfully merged

def factorize(num):
    factors = []
    for j in range(2, int(num ** 0.5) + 1):
        if num % j == 0:
            while num % j == 0:
                num //= j
            factors.append(j)
    if num > 1:
        factors.append(num)
    return factors

class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:
        max_num = max(nums)
        uf = UnionFind(max_num + 1)

        # Preprocess and group numbers by their gcd
        gcd_groups = defaultdict(list)
        for i, num in enumerate(nums):
            for f in factorize(num):
                gcd_groups[f].append(i)

        # Union numbers in each gcd group
        for group in gcd_groups.values():
            for i in range(len(group) - 1):
                uf.union(group[i], group[i + 1])

        # Count the size of each component using UnionFind
        component_sizes = defaultdict(int)
        for i in range(len(nums)):
            component_sizes[uf.find(i)] += 1

        # Return the size of the largest component
        return max(component_sizes.values())

