class DisjointSet {
    vector<int> rank, parent, size; 
public: 
    DisjointSet(int n) {
        rank.resize(n+1, 0); 
        parent.resize(n+1);
        size.resize(n+1); 
        for(int i = 0;i<=n;i++) {
            parent[i] = i; 
            size[i] = 1; 
        }
    }

    int findUPar(int node) {
        if(node == parent[node])
            return node; 
        return parent[node] = findUPar(parent[node]); 
    }
    int findsize(int node){
        return size[node];
    }

    void unionByRank(int u, int v) {
        int ulp_u = findUPar(u); 
        int ulp_v = findUPar(v); 
        if(ulp_u == ulp_v) return; 
        if(rank[ulp_u] < rank[ulp_v]) {
            parent[ulp_u] = ulp_v; 
        }
        else if(rank[ulp_v] < rank[ulp_u]) {
            parent[ulp_v] = ulp_u; 
        }
        else {
            parent[ulp_v] = ulp_u; 
            rank[ulp_u]++; 
        }
    }

    void unionBySize(int u, int v) {
        int ulp_u = findUPar(u); 
        int ulp_v = findUPar(v); 
        if(ulp_u == ulp_v) return; 
        if(size[ulp_u] < size[ulp_v]) {
            parent[ulp_u] = ulp_v; 
            size[ulp_v] += size[ulp_u]; 
        }
        else {
            parent[ulp_v] = ulp_u;
            size[ulp_u] += size[ulp_v]; 
        }
    }
}; 
class Solution {
public:
 unordered_set<int> findfact(int n){
     unordered_set<int> factors;
     for(int i=2;i<=sqrt(n);i++){
         if(n%i==0){
             while(n%i==0){n=n/i;}
             factors.insert(i);
         }
     }if(n>1)factors.insert(n);
     return factors;
 }
    int largestComponentSize(vector<int>& nums) {
        int n=nums.size();
        DisjointSet ds(n);
        unordered_map<int,int> lastind;
        for(int i=0;i<n;i++){
            unordered_set<int> factors=findfact(nums[i]);
            for(int fact:factors){
                if(lastind.find(fact)!=lastind.end()){
                    ds.unionBySize(i,lastind[fact]);
                }
                lastind[fact]=i;//store the last element that is divisible bt a factor and make an edge if it also divides the current number.
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            ans=max(ans,ds.findsize(i));
        }
        return ans;
    }
};
