            for(int fact:factors){
                if(lastind.find(fact)!=lastind.end()){
                    ds.unionBySize(i,lastind[fact]);
                }
                lastind[fact]=i;//store the last element that is divisible bt a factor and make an edge if it also divides the current number.
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            ans=max(ans,ds.findsize(i));
        }
