class Solution {
 private:
  class DisjointSet {
   public:
    DisjointSet(const int n) : parents_(n), groups_(n, 1), max_group_(1) {
      iota(parents_.begin(), parents_.end(), 0);
    }

    int find(const int i) {
      if (parents_[i] == i) {
        return i;
      }

      parents_[i] = find(parents_[i]);
      return parents_[i];
    }

    bool do_union(const int i, const int j) {
      const int parent_i = find(i);
      const int parent_j = find(j);
      if (parent_i == parent_j) {
        return false;
      }

      const int final_group = groups_[parent_i] + groups_[parent_j];
      max_group_ = max(max_group_, final_group);
      if (groups_[parent_i] < groups_[parent_j]) {
        parents_[parent_i] = parent_j;
        groups_[parent_j] = final_group;
        groups_[parent_i] = 0;
      } else {
        parents_[parent_j] = parent_i;
        groups_[parent_i] = final_group;
        groups_[parent_j] = 0;
      }
      return true;
    }

    int max_group() const {
      return max_group_;
    }

   private:
    vector<int> parents_;
    vector<int> groups_;
    int max_group_;
  };

 public:
  int largestComponentSize(const vector<int> &nums) {
    const int n = static_cast<int>(nums.size());
    unordered_map<int, vector<int>> factor_to_indices;
    for (int i = 0; i < n; ++i) {
      int num = nums[i];
      if (num % 2 == 0) {
        factor_to_indices[2].emplace_back(i);
        for (; num % 2 == 0; num /= 2) {
        }
      }
      for (int factor = 3; factor * factor <= num; factor += 2) {
        if (num % factor == 0) {
          factor_to_indices[factor].emplace_back(i);
          for (; num % factor == 0; num /= factor) {
          }
        }
      }
      if (num > 1) {
        factor_to_indices[num].emplace_back(i);
      }
    }

    DisjointSet ds(n);
    for (const auto &[_, indices] : factor_to_indices) {
      for (size_t i = 1; i < indices.size(); ++i) {
        ds.do_union(indices.front(), indices[i]);
      }
    }
