class Solution {
 private:
  class DisjointSet {
   public:
    DisjointSet(const int n) : parents_(n), ranks_(n) {
      iota(parents_.begin(), parents_.end(), 0);
    }

    int find(const int i) {
      if (parents_[i] == i) {
        return i;
      }

      parents_[i] = find(parents_[i]);
      return parents_[i];
    }

    bool do_union(const int i, const int j) {
      const int parent_i = find(i);
      const int parent_j = find(j);
      if (parent_i == parent_j) {
        return false;
      }

      if (ranks_[parent_i] < ranks_[parent_j]) {
        parents_[parent_i] = parent_j;
      } else if (ranks_[parent_i] > ranks_[parent_j]) {
        parents_[parent_j] = parent_i;
      } else {
        parents_[parent_i] = parent_j;
        ++ranks_[parent_j];
      }
      return true;
    }

   private:
    vector<int> parents_;
    vector<int> ranks_;
  };

 public:
  int largestComponentSize(const vector<int> &nums) {
    const int n = static_cast<int>(nums.size());
    const int max_num = *max_element(nums.begin(), nums.end());
    DisjointSet ds(max_num + 1);

    for (int i = 0; i < n; ++i) {
      int num = nums[i];
      if (num % 2 == 0) {
        ds.do_union(2, nums[i]);
        for (; num % 2 == 0; num /= 2) {
        }
      }
      for (int factor = 3; factor * factor <= num; factor += 2) {
        if (num % factor == 0) {
          ds.do_union(factor, nums[i]);
          for (; num % factor == 0; num /= factor) {
          }
        }
      }
      if (num > 1) {
        ds.do_union(num, nums[i]);
      }
    }

    int ret = 1;
    int parent_to_count[max_num + 1];
    memset(parent_to_count, 0, sizeof(parent_to_count));
    for (const int num : nums) {
      ret = max(ret, ++parent_to_count[ds.find(num)]);
    }
    return ret;
  }
};
