            rep1.parent = rep2i;
            rep2.size = rep1.size + rep2.size + 1;
            return rep2i;
        }
    }

    int representative(int node) {
        int root = node;
        while (root != nodes[root].parent) {
            root = nodes[root].parent;
        }

        int fixer = node;
        while (fixer != root) {
            int nextFixer = nodes[fixer].parent;
            nodes[fixer].parent = root;
            fixer = nextFixer;
        }

        return root;
    }

    int data(int node) {
        return nodes[representative(node)].data;
    }

    void setData(int node, int data) {
        nodes[representative(node)].data = data;
    }

private:
    struct SetNode {
        SetNode(int id): parent(id), data(0), size(1) {}
        int data;
        int size;
        int parent;
    };

    vector<SetNode> nodes;
};

class Solution {
public:
    int largestComponentSize(vector<int>& nums) {
        DisjointSet factorSets(0);
        int maxSize = 1;
        for (int n: nums) {
            if (n == 1) { continue; }
            int lastPrime = sieve.nextPrimeFactor(n);
            int representativeIndex = sieve.pi(lastPrime);
            factorSets.grow(representativeIndex);
            representativeIndex = factorSets.representative(representativeIndex);
            n /= lastPrime;
            while (n > 1) {
                lastPrime = sieve.nextPrimeFactor(n);
                int representativeIndex2 = sieve.pi(lastPrime);
                factorSets.grow(representativeIndex2);
                representativeIndex2 = factorSets.representative(representativeIndex2);
                if (representativeIndex == representativeIndex2) { 
                    n /= lastPrime;
                    continue;
                }
                int data1 = factorSets.data(representativeIndex);
                int data2 = factorSets.data(representativeIndex2);
                representativeIndex = factorSets.merge(representativeIndex, representativeIndex2);
                factorSets.setData(representativeIndex, data1 + data2);
                n /= lastPrime;
            }
            int size = factorSets.data(representativeIndex) + 1;
            factorSets.setData(representativeIndex, size);
            maxSize = max(maxSize, size);
        }
        return maxSize;
    }

private:
    static GrowingSieve sieve;
};

