from collections import defaultdict

class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:

        union_find = DisjointSetUnion(max(nums))

        for num in nums:
            for i in range(2, int(sqrt(num)) + 1):
                if(num % i == 0):
                    union_find.union(num, i)
                    union_find.union(num, num // i)

        greatest_comp_size = 0
        comps = defaultdict(int)
        for num in nums:
            parent = union_find.find(num)
            comps[parent] += 1
            greatest_comp_size = max(greatest_comp_size, comps[parent])
        return greatest_comp_size


class DisjointSetUnion:
    def __init__(self, n):
        self.parent = [i for i in range(n+1)]
        self.rank = [0] * (n+1)

    def find(self, x):
        if(self.parent[x] != x):
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)

        if(root_x == root_y):
            return

        rank_x = self.rank[root_x]
        rank_y = self.rank[root_y]

        if(rank_x > rank_y):
            self.parent[root_y] = root_x
        elif(rank_x < rank_y):
            self.parent[root_x] = root_y
        else:
            self.parent[root_y] = root_x
            self.rank[root_x] += 1

