class DisjointSet {
public:
    vector<int> parent, size;

    DisjointSet(int n) {
        parent.resize(n + 1);
        size.resize(n + 1);
        for (int i = 0; i <= n; i++) {
            parent[i] = i;
            size[i] = 1;
        }
    }
    int findUPar(int node) {
        if (node == parent[node])
            return node;
        return parent[node] = findUPar(parent[node]);
    }

    void unionBySize(int u, int v) {
        int ulp_u = findUPar(u);
        int ulp_v = findUPar(v);
        if (ulp_u == ulp_v) return;
        if (size[ulp_u] < size[ulp_v]) {
            parent[ulp_u] = ulp_v;
            size[ulp_v] += size[ulp_u];
        }
        else {
            parent[ulp_v] = ulp_u;
            size[ulp_u] += size[ulp_v];
        }
    }
};

class Solution {
public:
    int largestComponentSize(vector<int>& nums) {
        int size = *max_element(nums.begin(), nums.end());
        DisjointSet ds(size+1);

        for(int i=0; i<nums.size(); ++i){
            for(int j=2; j<=sqrt(nums[i]); ++j){
                if(nums[i]%j==0){
                    ds.unionBySize(nums[i], j);
                    ds.unionBySize(nums[i]/j, j);
                }
            }
        }

        map<int,int> k;
        int maxnodeconnected=0;
        for(auto it: nums){
            int p=ds.findUPar(it);
            k[p]++;
            maxnodeconnected=max(k[p],maxnodeconnected);
        }
        return maxnodeconnected;
    }
};
