require 'prime'

class UnionFindTree
  def initialize(n)
    @parent = (0...n).to_a
    @rank = [0] * n
  end

  def unite(a, b)
    root_a, root_b = root(a), root(b)
    return if root_a == root_b
    if @rank[root_a] < @rank[root_b]
      @parent[root_a] = root_b
    else
      @parent[root_b] = root_a
      @rank[root_a] += 1 if @rank[root_a] == @rank[root_b]
    end
  end

  def root(a)
    return a if @parent[a] == a
    @parent[a] = root(@parent[a])
  end
end

def largest_component_size(nums)
  union_find_tree = UnionFindTree.new(nums.max+1)
  nums.each {|num|
    Prime.prime_division(num).each {|prime, _|
      union_find_tree.unite(prime, num)
    }
  }
  max = 0
  counts = Hash.new(0)
  nums.each {|num|
    max = [max, counts[union_find_tree.root(num)] += 1].max
  }
  max
end
