class UnionFind {
public:
    UnionFind(int sz) : root(sz+1), rank(sz+1) {
        for (int i = 0; i < sz+1; i++) {
            root[i] = i;
            rank[i] = 1;
        }
    }

    int find(int x) {
        if (x == root[x]) {
            return x;
        }
        return root[x] = find(root[x]);
    }

    int unionSet(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);
        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                root[rootY] = rootX;
            } else if (rank[rootX] < rank[rootY]) {
                root[rootX] = rootY;
            } else {
                root[rootY] = rootX;
                rank[rootX] += 1;
            }
        }
    return rootY;
    }

    bool connected(int x, int y) {
        return find(x) == find(y);
    }

private:
    vector<int> root;
    vector<int> rank;
};

class Solution {
public:
    int largestComponentSize(vector<int>& nums) {
      int maxValue = *max_element(nums.begin(),nums.end());
      UnionFind dsu(maxValue);
      for (int num:nums){
          for(int factor=2;factor<int(sqrt(num))+1;factor++) {
             // cout<<factor<<" ";
              if(num%factor==0){

                  dsu.unionSet(num,factor);
                  dsu.unionSet(num,int(num/factor));
              }
          }

      }
      int maxG=0;
      unordered_map<int,int>gc;
      for(auto num:nums){
          int gid=dsu.find(num);
          int count=0;
          if(gc.find(gid)!=gc.end())
             count=gc[gid];
          gc[gid]++;
          maxG=max(maxG,count+1);
      }
     return maxG;
    }
};
