class Solution {
public:
    int prime[100001];
    void seive(){
        for(int i=0;i<=1e5;i++){
            prime[i]=i;
        }
        for(int i=2;i<=1e5;i+=2){
            prime[i]=2;
        }
        for(int i=3;i*i<=1e5;i++){
            if(prime[i]==i){
                for(int j=i*i;j<=1e5;j+=i){
                    prime[j]=i;
                }
            }
        }
    }

    void dfs(int node, vector<int>adj[], vector<int>&vis, int& cy){
        vis[node]=1;
        cy++;
        for(auto it: adj[node]){
            if(!vis[it]){
                dfs(it,adj,vis,cy);
            }
        }
    }

    int largestComponentSize(vector<int>& nums) {
        seive();
        map<int,set<int>>mp;
        int n=nums.size();
        for(int i=0;i<n;i++){
            int x=nums[i];
            while(x!=1){
                mp[prime[x]].insert(i);
                x/=prime[x];
            }
        }
        vector<int>adj[n];
        for(auto i: mp){
            int p=0;
            int x;
            for(auto j: i.second){
                if(p==0){
                    x=j;
                    p=1;
                }
                else{
                    adj[j].push_back(x);
                    adj[x].push_back(j);
                }
            }
        }
        int ans=0;
        vector<int>vis(n,0);
        for(int i=0;i<n;i++){
            if(!vis[i]){
                int cy=0;
                dfs(i,adj,vis,cy);
                ans=max(cy,ans);
            }
        }

        return ans;
    }
};







