import math as mt
MAXN = 10**5 + 5

spf = [i for i in range(MAXN)]

def sieve():
	spf[1] = 1

	for i in range(4, MAXN, 2):
		spf[i] = 2

	for i in range(3, mt.ceil(mt.sqrt(MAXN))):
		if (spf[i] == i):

			for j in range(i * i, MAXN, i):

				if (spf[j] == j):
					spf[j] = i

def factors(x):
	ret = set()
	while (x != 1):
		ret.add(spf[x])
		x = x // spf[x]

	return ret

sieve()
class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:
        df = dict()
        uf = DSU(len(nums))
        for i,x in enumerate(nums):
            for f in factors(x):
                if(f in df): uf.union(i,df[f])
                else: df[f] = i

        return uf.mx;


class DSU:
    def __init__(self, N):
        self.par = list(range(N))
        self.sz = [1] * N
        self.mx = 1
    def find(self, x):
        if self.par[x] != x:
            self.par[x] = self.find(self.par[x])
        return self.par[x]
    def union(self, x, y):
        xr, yr = self.find(x), self.find(y)
        if xr == yr:
            return False
        if self.sz[xr] < self.sz[yr]:
            xr, yr = yr, xr
        self.par[yr] = xr
        self.sz[xr] += self.sz[yr]
        self.mx = max(self.mx,self.sz[xr])
        return True
