class Solution {
public:
    vector<vector<int>> adj;        // adjacency list of indexes connected with each other
    vector<int> vis;
    map<int, int> pr_ind;           // index of a number divisible by this prime

    int dfs(int i, int par){
        int size = 1;
        vis[i] = 1;
        for(int& ch : adj[i]){
            if(ch != par and vis[ch] == 0){
                size += dfs(ch, i);
            }
        }
        return size;
    }

    int largestComponentSize(vector<int>& a) {
        int n = a.size();
        adj.resize(n);
        for(int i = 0; i < n; ++i){
            int num = a[i];

            // find the prime factorisation of a[i]
            // connect a[i] with all the numbers which share the same prime numbers as divisors
            // CATCH : We dont have to connect a[i] with all the numbers that share the same prime number
            // connecting with only one of those numbers will be fine as through that number we have automatically
            // made an indirect connection with all the other numbers ( Imagine like a Linked List )

            {
                for(long k = 2; k * k <= num; ++k){
                    if(num % k == 0){
                        // add connection
                        // pr_ind me agar koi index pehle se hai to usse connect krdo
                        // if pr_ind has some former index then connect 'i' with that index

                        // otherwise any number divisible by prime number K has not been 
                        // found before hence we cannot make any connection
                        if(pr_ind.count(k) == 0){
                            pr_ind[k] = i;
                        } else{
                            adj[pr_ind[k]].push_back(i);
                            adj[i].push_back(pr_ind[k]);
                        }

                        // remove all occ of this prime number
                        while(num % k == 0){
                            num /= k;
                        }

                    }
                }
                if(num > 1){
                    if(pr_ind.count(num) == 0){
                        pr_ind[num] = i;
                    } else{
                        adj[pr_ind[num]].push_back(i);
                        adj[i].push_back(pr_ind[num]);
                    }
                }

            }
        }
        // find the maximum component size
        int ans = 0;
        vis.resize(n);
        for(int i = 0; i < n; ++i){
            if(!vis[i]){
                int size = dfs(i, -1);
                ans = max(ans, size);
            }
        }

        return ans;
    }
};
