import math
from collections import defaultdict, Counter
from typing import List


class UnionSet:
    def __init__(self, n):
        self.parent = list(range(n))

    def find(self, x):
        curr = self.parent[x]
        while curr != self.parent[curr]:
            curr, self.parent[curr] = self.parent[curr], self.parent[self.parent[curr]]
        return self.parent[curr]

    def union(self, x, y):
        xr, yr = self.find(x), self.find(y)
        self.parent[xr] = yr

@cache
def primeSet(n): # Time: O(sqrt(n)*logn), Space: O(logn), a prime set is a set of prime numbers that can express a composite number
    for i in range(2, int(math.sqrt(n) + 1)):
        if n % i == 0:
            return primeSet(n // i) | {i}
    return {n}


class Solution:
    def largestComponentSize(self, nums: List[int]) -> int:
        n = len(nums)
        unionFind = UnionSet(n)
        primes = defaultdict(list)

        for i, num in enumerate(nums):  # { factor: [indexes for which has the factor] }
            for q in primeSet(num):
                primes[q].append(i)

        for _, indexes in primes.items(): # unionFind for all indexes with a similar factor
            for i in range(len(indexes)-1):
                unionFind.union(indexes[i], indexes[i + 1])

        return max(Counter([unionFind.find(i) for i in range(n)]).values()) # find parent index with the largest size
