vector<int>par,siz;
class Disjoint{
    public:
    Disjoint(int n){
        par.resize(n+1);
        siz.resize(n+1);
        int i;
        for(i=0;i<=n;i++){
            par[i]=i;
            siz[i]=1;
        }
    }
    int find(int node){
        if(node==par[node]) return node;
        return par[node]=find(par[node]);
    }
    void join(int u,int v){
        int ultu=find(u),ultv=find(v);
        if(ultu!=ultv){
            if(siz[ultu]>=siz[ultv]){
                par[ultv]=ultu;
                siz[ultu]+=siz[ultv];
            }
            else{
                 par[ultu]=ultv;
                siz[ultv]+=siz[ultu];
            }
        }
    }
};
class Solution {
public:
    int largestComponentSize(vector<int>& nums) {
        int num=*max_element(nums.begin(),nums.end());
        int maxi=1,i,j;
        Disjoint ds(num);
        for(i=0;i<nums.size();i++){
            for(j=2;j<=sqrt(nums[i]);j++){
                if(nums[i]%j==0){
                ds.join(nums[i],j);
                ds.join(j,nums[i]/j);
                }
            }
