	def prime(x):
		factors = set()
		for i in range(2, int(sqrt(x))+1):
			while(x%i==0):
				x//=i
				factors.add(i)
		return factors|{x} if x>1 else factors

	uf, pf = list(range(len(nums))), defaultdict(list)
	for i, x in enumerate(nums):
