class Solution {
public:
    bool vis[100005];
    void dfs(int x , set<int> adj[], int &cnt, unordered_map<int,int>& m){
        vis[x]=1;
        if(m.count(x)) cnt++;
        for(auto it : adj[x]){
            if(!vis[it]){
                dfs(it, adj, cnt ,m);
            }
        }

    }
