int flag=0,mx=1e5+10;
vector<int> spf;
class Solution {
    vector<int> par;
public:
    Solution(){
        if(!flag){
            flag=1;
            spf.resize(mx,2);
            for(int i=0;i<mx;i++)
                if(i&1)
                    spf[i]=i;
            int sq=sqrt(mx);
            for(int i=3;i<=sq;i+=2){
                if(spf[i]==i){
                    for(int j=i*i;j<mx;j+=i)
                        spf[j]=i;
                }
            }
        }
    }

    int findPar(int p){
        if(par[p]==p)
            return p;
        return par[p]=findPar(par[p]);
    }

    int largestComponentSize(vector<int>& a) {
        par.resize(mx);
        for(int i=0;i<mx;i++)
            par[i]=i;

        sort(a.begin(),a.end());
        for(auto e:a){
            vector<int> p;
            int mn=e;
            while(e>1){
                int pr=spf[e];
                while(e%pr==0)
                    e/=pr;
                p.push_back(findPar(pr));
                mn=min(mn,findPar(pr));
            }
            mn=findPar(mn);
            for(auto e:p)
                par[e]=mn;
        }

        int ans=0;
        unordered_map<int,int> mp;
        for(auto e:a){
            mp[findPar(spf[e])]++;
            ans=max(ans,mp[findPar(spf[e])]);
        }

        return ans;
    }
};
