class Solution 
	{
	private:
	int findPar(vector<int>&parent,int node)
	{
		if(node==parent[node])return node;
		return parent[node] = findPar(parent,parent[node]);
	}
	void unite(vector<int>&size,vector<int>&parent,int u,int v)
	{
		u = findPar(parent,u);
		v = findPar(parent,v);
		if(size[u]<size[v])
		{
			parent[u] = v;
			size[v]+=size[u];
		}
		else
		{
			parent[v] = u;
			size[u]+=size[v];
		}
	}
	void createSieve(vector<int>&sieve)
	{
		for(int i=2;i*i<=100000;i++)
		{
			if(sieve[i]==-1)
			{
				for(int j=i*i;j<=100000;j+=i)
				{
					sieve[j] = i;
				}
			}
		}
	}
public:
	int largestComponentSize(vector<int>& nums) 
	{
		int n = nums.size();
		vector<int>sieve(100001,-1);
		createSieve(sieve);
		map<int,set<int>>mp;
		for(int i=0;i<n;i++)
		{
			int x = nums[i];
			int y = nums[i];
			vector<int>temp;
			while(sieve[x]!=-1)
			{
				temp.push_back(sieve[x]);
				x/=sieve[x];
			}
			temp.push_back(x);
			for(auto j:temp)mp[j].insert(i);
		}
		int m = mp.size();
		vector<int>parent(n+m,0),size(n+m,1);
		for(int i=0;i<n+m;i++)parent[i] = i;
		int i = n-1;
		vector<pair<int,int>>edges;
		for(auto it:mp)
		{
			i++;
			for(auto j:it.second)edges.push_back({i,j});
		}
		for(auto i:edges)
		{
			int x = findPar(parent,i.first);
			int y = findPar(parent,i.second);
			if(x!=y)unite(size,parent,i.second,i.first);
		}
		map<int,int>z;
		for(int i=0;i<n;i++)z[findPar(parent,parent[i])]++;
		int maxi = 0;
		for(auto i:z)maxi = max(maxi,i.second);
		return maxi;
	}
