class Solution {
public:
    static const int N =  1e5 +5;
    int n; 
    int vis[N] = {0};
    int par[N];
    int rank[N] = {1};
    vector<int>prime;

    void init(){
        for( int i = 0 ; i<N ; ++i ){
             par[i] = i;
            rank[i] = 1;
            if(vis[i]==1 || i<2 )continue;
            for( int j = i ; j<N ; j = 1ll*(j+i)){
                vis[j] = 1;
            }
            prime.emplace_back(i);
        }
    }


    int find_par( int node ){
        if(node == par[node])return node;
        return par[node] = find_par(par[node]);
    }


    void merge( int x , int y ){
        x = find_par(x);
        y = find_par(y);

        if(x==y)
            return ;

        if(rank[x]==rank[y]){
            par[y] = x;
            rank[x] += 1;
        }
        else{
            if(rank[x] >  rank[y]){
                par[y] = x;
                rank[x] += rank[y];
            }
            else{
                par[x] = y;
                rank[y] += rank[x];
            }
        }
    }

    int largestComponentSize(vector<int>& nums) {
        init();

        unordered_map<int , int >int_mp;

        for( int i = 0 ; i<nums.size() ; ++i  ){
              int_mp[nums[i]] = i;
        }




        for( int p : prime  ){
            vector<int>temp;
            for( int x = p ; x<N ; x += p ){
                if(int_mp.find(x) != int_mp.end())
                      temp.emplace_back(int_mp[x]);
            }

            for( int i = 1  ; i<temp.size() ; ++i ){
                merge(temp[i] , temp[i-1]);
            }

            //cout<<temp.size()<<"\n";
