        int cnt = 1 ;
        unordered_map<int , int>mp;
        for( int x = 0  ; x<nums.size() ; ++x ){
            int p  = find_par(x);
            mp[p] += 1;
            cnt = max( cnt , mp[p]);
        }
        return cnt ;

        //return 0;
    }
};
