            }
            var n1 = n
            for s in simpleNums {
                if s > sMax || n1 < 2 { break }
                if n1 % s == 0 {
                    addDivider(s, i)
                    while n1 % s == 0 { n1 /= s }
                }
            }
            if n1 > 1 {
                addDivider(n1, i)
            }
        }
        return simplesMap
    }
}
