class Solution:
    def numTrees(self, n: int) -> int:

        cache = [-1]*(n+1)
        cache[0] = 1
        cache[1] = 1
        return self.helper(n, cache)

    def helper(self, n, cache):
        if cache[n] != -1:
            return cache[n]


        unique_trees = 0
        for i in range(n):
            left_trees = self.helper(i, cache)
            right_trees = self.helper(n - i -1, cache)
            unique_trees += left_trees*right_trees
        cache[n] = unique_trees
        return unique_trees
