void recur(int n,unordered_map<int,int> &mp)
    {
	//base case
        if(n==0 || n==1) return;

		//recursive call for n-1
        recur(n-1,mp);
		//variable to calculate answer for current n
        int val = 0;

		//for every i such that 1<=i<=n, we'll calculate number of
		//BSTs made by taking node with value i as root and
		//add it to our val
        for(int i=1;i<=n;i++)
        {
			//to calculate no of BSTs after making ith node as root
