    public int numTrees(int n) {    // time O(n ^ 2) space O(n)
        int[] f = new int[n+1];
        f[0] = 1; 
        f[1] = 1;
        if(n <= 1) return 1; // corner case
        for(int r = 2; r<= n; r++){
            for(int k = 1; k <= r; k++){
                f[r] += f[k-1]*f[r-k];
            }
        }        
        return f[n];        
    }

