bool rec(string &a, string &b, string &ist, int i = 0, int j = 0, int k = 0)
{
int n = a.length(), m = b.length();
if (n + m != ist.length())
return false;
if (i == n && j == m)
    return true; // base case

bool left, right;
left = i < n ? a[i] == ist[k] : false;
right = j < m ? b[j] == ist[k] : false;

if (left && right)
    return rec(a, b, ist, i + 1, j, k + 1) || rec(a, b, ist, i, j + 1, k + 1);

else if (left)
    return rec(a, b, ist, i + 1, j, k + 1);

else if (right)
    return rec(a, b, ist, i, j + 1, k + 1);

else
    return false;
}
bool isInterleave(string s1, string s2, string s3)
{
return rec(s1, s2, s3);
}
