{
    if (b[j] == ist[n + j])
        prev[j] = prev[j + 1];
    else
        prev[j] = false;
} // base case i==n

for (int i = n - 1; i >= 0; i--)
{
    prev[m] = a[i] == ist[m + i];

    for (int j = m - 1; j >= 0; j--)
    {
        bool left, right;
        left = a[i] == ist[i + j];
        right = b[j] == ist[i + j];

        if (left && right)
            prev[j] = prev[j] || prev[j + 1];

        else if (right)
            prev[j] = prev[j + 1];

        else if (!left)
            prev[j] = false;
    }
}

return prev[0];
}
