```
class Solution {
public:
    bool isInterleave(string s1, string s2, string s3) {
        int m = s1.size();
        int n = s2.size();
        int k = s3.size();

        if (m + n != k) {
            return false;
        }

        vector<vector<bool>> dp(m + 1, vector<bool>(n + 1, false));

        // base case
        dp[0][0] = true;

        // Fill the dp table
        for (int i = 0; i <= m; ++i) {
            for (int j = 0; j <= n; ++j) {
                int l = i + j - 1; // Current character in s3

                if (i > 0 && s1[i - 1] == s3[l]) {
                    dp[i][j] = dp[i][j] || dp[i - 1][j];
                }

                if (j > 0 && s2[j - 1] == s3[l]) {
                    dp[i][j] = dp[i][j] || dp[i][j - 1];
                }
            }
        }

        return dp[m][n];
    }
};
