bool isInterleave(string s1, string s2, string s3) {
	int n = s1.size(), m = s2.size();
	if(n + m != s3.size()) return false;


	vector<bool> dp(m+1);
	dp[0] = true;

	for(int len1 = 0 ; len1 <= n ; len1++) {
		for(int len2 = 0 ; len2 <= m ; len2++) {

			if(!len1 and !len2) continue;
			int len3 = len1 + len2;
			bool match1 = (len1) ? s1[len1-1] == s3[len3-1] and dp[len2] : false;
			bool match2 = (len2) ? s2[len2-1] == s3[len3-1] and dp[len2-1] : false;
			dp[len2] = match1 or match2;
		}
	}

	return dp[m];
}
