        if(i < s1.length() && s1[i] == s3[k])
            res |= canInterleave(k + 1, i + 1, j, s1, s2, s3);

        // Check if we can match s2[j] to s3[k]
        // and if we can, can we get answer by matching them
        if(j < s2.length() && s2[j] == s3[k])
            res |= canInterleave(k + 1, i, j + 1, s1, s2, s3);

        return res;
    }

public:
    bool isInterleave(string s1, string s2, string s3) {
        return canInterleave(0, 0, 0, s1, s2, s3);
    }
};
